const MyModule=(()=>{"use strict";let e=e=>4*e,t=e=>e*e,l=document.querySelector("#btnPerimCuadrado"),r=document.querySelector("#btnAreaCuadrado"),n=document.querySelector("#btnPerimTriangulo"),d=document.querySelector("#btnAreaTriangulo"),i=document.querySelector("#btnDiamCirculo"),u=document.querySelector("#btnPerimCirculo"),a=document.querySelector("#btnAreaCirculo");l.addEventListener("click",()=>{let t=Number(document.getElementById("inputSide").value),l=`El perimetro del cuadrado es: ${e(t)} cm`;document.getElementById("result").innerText=l}),r.addEventListener("click",()=>{let e=Number(document.getElementById("inputSide").value),l=`El Area del cuadrado es: ${t(e)} cm^2`;document.getElementById("result").innerText=l});let c=(e,t,l)=>e+t+l,m=(e,t)=>e*t/2;n.addEventListener("click",()=>{let e=document.getElementById("inputSide1"),t=document.getElementById("inputSide2"),l=document.getElementById("inputSide3");e=Number(e.value),t=Number(t.value),l=Number(l.value);let r=`El perimetro del triangulo es: ${c(e,t,l)} cm`;document.getElementById("result").innerText=r}),d.addEventListener("click",()=>{let e=document.getElementById("inputBase"),t=document.getElementById("inputAltura");e=Number(e.value),t=Number(t.value);let l=`El area del triangulo es: ${m(e,t)} cm^2`;document.getElementById("result").innerText=l});let E=e=>2*e,o=e=>E(e)*Math.PI,s=e=>e*e*Math.PI;i.addEventListener("click",()=>{let e=document.getElementById("radio1");e=Number(e.value);let t=`El diametro del circulo es: ${E(e)} cm`;document.getElementById("result").innerText=t}),u.addEventListener("click",()=>{let e=document.getElementById("radio1");e=Number(e.value);let t=`El perimetro del circulo es: ${o(e)} cm`;document.getElementById("result").innerText=t}),a.addEventListener("click",()=>{let e=document.getElementById("radio1");e=Number(e.value);let t=`El area del circulo es: ${s(e)} cm`;document.getElementById("result").innerText=t})})();

